fun main(args: Array<String>) {

    var openingCount = 0
    var closingCount = 0
    input.forEach {
        if (it == '(') openingCount++
        if (it == ')') closingCount++
    }
    println(openingCount-closingCount)

    var i = 0
    var position = 0
    for (it in input) {
        position ++
        if (it == '(') i++
        if (it == ')') i--
        if(i<0) break
    }
    println(position)
}